package com.mytt.componet;

import com.mytt.rsacode.rsa.model.ResultInfo;
import com.mytt.rsacode.rsa.model.RsaVo;
import com.mytt.rsacode.rsa.client.RSAClient;
import com.mytt.rsacode.rsa.utils.FastJsonUtils;

import java.util.Map;

public class RSAResponse {
    public static final String serverPrivateKey="";
    public static final String clientPublicKey="";


    public static void verifyBody(RsaVo rsaVo) throws Exception {
        RSAClient.verifyBody(rsaVo, clientPublicKey, serverPrivateKey);
    }

    public static Map<String, Object> buildBody(ResultInfo resultInfo) throws Exception {
        Map<String, Object> resultMap = RSAClient.buildBody(FastJsonUtils.objectToJson(resultInfo), clientPublicKey, serverPrivateKey);
        return resultMap;
    }
}
